mapper.select:
select p.*, s.pkg_title, s.pkg_description, s.price, s.revision, s.workdays, t.basic_pkg_title, t.basic_pkg_description, t.basic_revision, t.basic_price, t.basic_workdays, t.economy_pkg_title, t.economy_pkg_description, t.economy_revision, t.economy_price, t.economy_workdays, t.premium_pkg_title, t.premium_pkg_description, t.premium_revision, t.premium_price, t.premium_workdays, u.user_name, u.thumbnail_store_filename from project p left outer join project_package_single s on p.project_id = s.project_id left outer join project_package_triple t on p.project_id = t.project_id left outer join user u on p.creator_id = u.user_id
p.category1 = #{category1}
p.category1 = #{category1} AND p.category2_pj = #{category2_pj}
ORDER BY project_date DESC
limit ${(skip-1)*16},16
